sp_widget.client_script:
function($scope,$rootScope,spUtil,spModal) {
	/* widget controller */
	var c = this;

	$rootScope.$on('selectNote',function(event,data) {
		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID
		}).then(function(r) {
			c.data.title = r.data.noteObj.title;
			c.data.note = r.data.noteObj.note;
			c.data.noteID = r.data.noteObj.sys_id;
		});
	});

	c.updateNote=function(updateType){
		c.server.get({
			action:'updateNote',
			noteID:c.data.noteID,
			noteBody:c.data.note,
			noteTitle:c.data.title		
		}).then(function(response){

		});
$rootScope.$emit("updateNote",noteTitle,noteBody);
			console.log("updateNote: "+ noteTitle);
spModal.alert("updateNote"+noteTitle);
	}
}

sp_widget.script:
(function() {

	if(input &&input.noteID){
		var note=new GlideRecord('x_snc_createnotes_note');
		if(note.get(input.noteID)){

			if(input.action=="getNote"){

				data.noteObj={};
				data.label=note.getLabel();
				$sp.getRecordDisplayValues(data.noteObj,note,'title,note,sys_id');
				gs.addInfoMessage("hello aashish"+data.label);


			}

			else if(input.action=='updateNote'){

				note.title=input.noteTitle;
				note.note=input.noteBody;
				note.update();

			}

		}	



	}

})();
