sp_widget.client_script:
function($rootScope,$scope,spUtil) {
  /* widget controller */




  var c = this;

//Call the listener 

$rootScope.$on('selectNote',function(event,data){

console.log("note body " + data );



c.server.get({

action:"getNotes",
noteID: data
}).then(function(response){


c.data.title=response.data.note.title;
c.data.noteID=response.data.note.sys_id;
c.data.note = response.data.note.note;

});

});
c.updateNote=function(typ){
	c.server.get({

		action:'updateNote',
		noteID:c.data.noteID,
		noteBody:c.data.note,
		noteTitle:c.data.title


	}).then(function(r){
		if(typ=='title'|| typ=='body')
		{

			$rootScope.$emit('updateNote',r.data);
						//console.log("response " + r);
}
});
}



}

sp_widget.script:
(function() {
  /* populate the 'data' object */

  if (input && input.noteID) {
    var note = new GlideRecord('x_snc_createnotes_note');
    if (note.get(input.noteID)) {
      if (input.action == 'getNotes') {
        data.note = {};
        $sp.getRecordValues(data.note, note, "title, note, sys_id");
      }
else if (input.action == 'updateNote') { 
	gs.addErrorMessage("hello");	

	note.title = input.noteTitle; 
		note.note = input.noteBody; 
    note.update(); 

    data.title=note.title.toString();
    data.note=note.note.toString();
	} 
	}
	}
})();
