sp_widget.client_script:
function($rootScope,$scope,spModal) {
  /* widget controller */
  var c = this;

c.data.pos=0;
c.selectItem=function(idx){
//to get the data from server to client 
c.data.pos=idx;
var id=c.data.notes[idx].sys_id;


//basically 1$rootScope is is parent scope you can defien property on parent scope like global varibale snad can use in any other widget

$rootScope.noteID=id;
// sending id to rootscope with value of id 

$rootScope.$emit('selectNote',id);

  console.log("Note id" +id);
}


//$on

$rootScope.$on('updateNote',function(event,data){
  console.log(event);
c.data.notes[c.data.pos].title=data.title;
c.data.notes[c.data.pos].note=data.note;

});

}

sp_widget.script:
(function(){
data.notes = [];
var noteGR = new GlideRecord('x_snc_createnotes_note');

noteGR.orderByDesc('sys_created_on');
noteGR.query();
while (noteGR.next()) {
var noteObj = {};
//use service portal helper method to get some display values
$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id,note');
//get the first 20 characters of the description
//gs.addInfoMessage('NoteObj'+JSON.stringify(noteObj));
//noteObj.note = noteGR.getValue('note').slice(0,20);
//push the populated obj into the array
data.notes.push(noteObj);

}
})();
