sp_widget.client_script:
function($scope,$rootScope,spUtil) {
	/* widget controller */
	var c = this;

	c.selectItem=function(index){

		var id=	c.data.notes[index].sys_id;
		console.log("Note iD: "+id);
		$rootScope.noteID=id;
		$rootScope.$emit('selectNote',id);
spUtil.addInfoMessage("NoteId  "+$rootScope.noteID);

	}
$rootScope.$on('updateNote',function(event,data){




	c.server.update().then(function(r){});

});




}

sp_widget.script:
(function() {
	//create an array to populate without notes
	data.notes=[];
	var gr = new GlideRecord('x_snc_createnotes_note');
	gr.addQuery('user',gs.getUserID());
	//	gs.addInfoMessage("User"+gs.getUserID()+gs.getUser().getID());

	gr.orderByDesc('sys_created_on');
	gr.query();
	while(gr.next()){
		var nodeObj={};
		$sp.getRecordDisplayValues(nodeObj,gr,'number,title,sys_id');

		nodeObj.note=gr.getValue('note').slice(0,20);
		data.notes.push(nodeObj);
	}
})();
